var jsondata = JSON.parse(value);
var keylist = Object.keys(jsondata);
var res = [];
for (var i = 0; i < keylist.length; i++) {
	var g = keylist[i];
	var ip_data = Object(jsondata[g]["peers"]);
	for (var j = ip_data.length - 1; j >= 0; j--) {
		var item = {"name": keylist[i], "ip": jsondata[g]["peers"][j]["server"], "id": jsondata[g]["peers"][j]["id"]};
	  	res.push(item);
	}
}
return JSON.stringify(res);
